# -*- coding: iso-8859-1 -*-# simple example to test if a redirect worksfrom   _GTW            import GTWimport _GTW._NAV.Baseclass Redirect (GTW.NAV.Page) :    """Always redirect"""    def _view (self, handler) :        cls = self.top.HTTP.Status.Table [self.code]        raise cls (self.redirect_to)    # end def _view# end class Redirectclass Error (GTW.NAV.Page) :    """Display error"""    def _view (self, handler) :        cls = self.top.HTTP.Status.Table [self.code]        raise cls    # end def _view# end class Redirect### __END__ Redirect