# -*- coding: iso-8859-1 -*-# simple example to test if a redirect worksfrom   _GTW            import GTWimport _GTW._NAV.Baseclass Redirect (GTW.NAV.Page) :    """Always redirect"""    def _view (self, handler) :        if self.code == 301 :            cls = self.top.HTTP.Redirect_301        else :            cls = self.top.HTTP.Redirect_302        raise cls (self.redirect_to)    # end def _view# end class Redirect### __END__ Redirect